import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Form() {
  const [productName, setProductName] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [productStock, setProductStock] = useState('');
  const [productCategory, setProductCategory] = useState('');
  const [products, setProducts] = useState([]);
  const style = {
    margin: '10px',
  };

  const addProduct = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        name: productName,
        price: productPrice,
        stock: productStock,
        category: productCategory,
      };
      await axios.post('http://localhost:5000/api/form', payload);
      // Clear input fields after successful submission
      setProductName('');
      setProductPrice('');
      setProductStock('');
      setProductCategory('');
      // Refresh the list of products
      fetchProducts();
    } catch (error) {
      console.error('Error creating product:', error);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/form/');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/form/${id}`);
      // Refresh the list of products after deletion
      fetchProducts();
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div style={{ border: '2px black solid', margin: 'auto', borderRadius: '5px', width: '50%' }} className="container">
      <form onSubmit={addProduct}>
        <div className="form">
          <h1 style={{ color: 'black' }}>Products</h1>
          <div>
            <input
              style={style}
              type="text"
              placeholder="Product Name"
              value={productName}
              onChange={(e) => setProductName(e.target.value)}
            />
            <input
              style={style}
              type="number"
              placeholder="Price"
              value={productPrice}
              onChange={(e) => setProductPrice(e.target.value)}
            />
            <input
              style={style}
              type="number"
              placeholder="Stock"
              value={productStock}
              onChange={(e) => setProductStock(e.target.value)}
            />
            <input
              style={style}
              type="text"
              placeholder="Category"
              value={productCategory}
              onChange={(e) => setProductCategory(e.target.value)}
            />
            <button type='submit' style={style}>Add Product</button>
            {/* Remove the Update and Delete buttons here */}
          </div>
        </div>
      </form>

      <div>
        <table className="table mx-auto">
          <tbody>
            <tr>
              <th>Product Name</th>
              <th>Price</th>
              <th>Stock</th>
              <th>Category</th>
              <th>Actions</th>
            </tr>
            {products.map((product) => (
              <tr key={product._id}>
                <td>{product.name}</td>
                <td>{product.price}</td>
                <td>{product.stock}</td>
                <td>{product.category}</td>
                <td>
                  <Link to={`/update/${product._id}`} className="btn btn-sm btn-success me-2">Update</Link>
                  <button className="btn btn-danger" onClick={() => handleDelete(product._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Form;



